import React from 'react';
import useSound from 'use-sound';
// import baseloop from './sounds/baseloop.wav';
// import pookie from './sounds/pookie.mp3';
// import disco from './sounds/disco.mp3';Å¾
import loaded from './loaded.mp3';

const useKeyboardBindings = map => {
    React.useEffect(() => {
      const handlePress = ev => {
        const handler = map[ev.key];

        if (typeof handler === 'function') {
          handler();
        }
      };

      window.addEventListener('keydown', handlePress);

      return () => {
        window.removeEventListener('keydown', handlePress);
      };
    }, [map]);
  };

const Midi = () => { 
    const [play, { stop }] = useSound(loaded, {
        sprite: {
            base1: [0, 1800],
            base2: [1800, 2100],
            pookie: [3000]
          },
    });

    useKeyboardBindings({
        1: () => play({ id: 'base1' }),
        2: () => play({ id: 'base2' }),
        3: () => play({ id: 'pookie' }),
        4: () => play({ id: 'cowbell' }),
      });

    // useKeyboardBindings({
    //     1: () => play({})
    // })


        return (
            <div className="midicontainer">
                <div className="buttonrow">
                    <>
                    <button arian-label="kick" onMouseDown={() => play({id: 'base1'}) } onMouseLeave={stop} className="square">1</button>

                    <button arian-label="hihat" onMouseDown={() => play({id: 'base2'})} className="square">2</button>
                    <button className="square">3</button>

                    <button arian-label="snare" onMouseDown={() => play({id: 'pookie'})} className="square">4</button>
                    <button arian-label="cowbell" onMouseDown={() => play({id: 'cowbell'})} className="square">5</button>
                    </>
                    <button className="square">6</button>
                    <button className="square">7</button>
                    <button className="square">8</button>
                </div>
                <div className="buttonrow">
                    <button className="square">a</button>
                    <button className="square">s</button>
                    <button className="square">d</button>
                    <button className="square">f</button>
                    <button className="square">g</button>
                    <button className="square">h</button>
                    <button className="square">j</button>
                    <button className="square">k</button>
                </div>
                <div className="buttonrow">
                    <button className="square">31</button>
                    <button className="square">32</button>
                    <button className="square">33</button>
                    <button className="square">34</button>
                    <button className="square">35</button>
                    <button className="square">36</button>
                    <button className="square">37</button>
                    <button className="square">38</button>
                </div>
            </div>
        )
}



export default Midi
